    private void jSearchActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jSearchActionPerformed
        // TODO add your handling code here:
        if(txt.getText().trim().equals("")==true){
            JOptionPane.showMessageDialog(rootPane, "Hãy nhập từ cần tra");
        }
        else {
            if(DM.dictionaryLookup(txt.getText().trim())==true){
                String a=DM.JMean1(txt.getText().trim());
                txtResult.setText(a);
            }
            else {
                JOptionPane.showMessageDialog(rootPane, "Từ không có trong từ điển!");
                txtResult .setText("");
            }
        }
    }//GEN-LAST:event_jSearchActionPerformed

