    DefaultListModel<String> mod;
    LinkedList<Word> arr = new LinkedList<>();
    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        mod = new DefaultListModel<>();
        arr = DM.filterWord("");
        list.setModel(mod);
        arr.forEach((i) -> {
            mod.addElement(i.getWord());
        });
    }//GEN-LAST:event_formWindowOpened

    private void txtKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtKeyTyped
        String text = txt.getText();
        text+=evt.getKeyChar();
        text= text.toLowerCase().trim();
        mod = new DefaultListModel<>();
        arr = DM.filterWord(text);
        list.setModel(mod);
        arr.forEach((Word i) -> {
            mod.addElement(i.getWord());
        });
        
            if (mod.size()>0) {
                list.setSelectedIndex(0);
                javax.swing.JScrollBar sb = sp.getVerticalScrollBar();
                sb.setValue(0);
                return ;
            }
        
        txtResult.setText("THIS WORD NOT FOUND !");
        arr = null;
    }//GEN-LAST:event_txtKeyTyped

    private void listValueChanged(javax.swing.event.ListSelectionEvent evt) {//GEN-FIRST:event_listValueChanged
        // TODO add your handling code here:
        if(!list.isSelectionEmpty()){
            int index = list.getSelectedIndex();
            Word obj=arr.get(index);
            txtResult.setText(obj.getMean());
        }
    }//GEN-LAST:event_listValueChanged

