        // TODO add your handling code here:
        if(!list.isSelectionEmpty()){
            int index = list.getSelectedIndex();
            Word obj=arr.get(index);
            txtResult.setText(obj.getMean());
            new DictionaryManagement(obj.getWord());
        }
        
